// üåê Importamos React y herramientas de routing
import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// üß© Importamos componentes de la app
import Productos from "./components/Productos";
import Carrito from "./components/Carrito";
import Pago from "./components/Pago";
import EstadoPago from "./components/EstadoPago";
import Categoria from "./components/Categoria"; // ‚Üê nuevo import

// üåç Contexto global para el carrito
import { CarritoProvider } from "./context/CarritoContext";

// üé® Importamos estilos globales
import "./App.css";

// üîó Importamos la URL del backend desde .env
const API_URL = process.env.REACT_APP_API_URL;

const App = () => {
  return (
    <CarritoProvider>
      <Router>
        <Routes>
          {/* üè† Ruta principal: cat√°logo, categor√≠as, carrito y pago */}
          <Route
            path="/"
            element={
              <div>
                <Categoria /> {/* ‚úÖ Se muestra al inicio */}
                <Productos apiUrl={API_URL} />
                <Carrito apiUrl={API_URL} usuarioId={"123456"} />
                <Pago apiUrl={API_URL} />
              </div>
            }
          />

          {/* üìÅ Ruta din√°mica para productos por categor√≠a */}
          <Route
            path="/productos/:nombre"
            element={<Productos apiUrl={API_URL} />}
          />

          {/* üìÑ Ruta din√°mica para mostrar estado del pago */}
          <Route
            path="/estado/:reference"
            element={<EstadoPago apiUrl={API_URL} />}
          />
        </Routes>
      </Router>
    </CarritoProvider>
  );
};

export default App;
