import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const EstadoPago = () => {
  const { reference } = useParams(); // üì¶ Referencia del pago desde la URL
  const [estado, setEstado] = useState(null); // üîÅ Estado actual del pago
  const [ultimaConsulta, setUltimaConsulta] = useState(null); // ‚è±Ô∏è Timestamp de la √∫ltima verificaci√≥n

  useEffect(() => {
    let intervalo;

    const consultarEstado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);

        if (res.ok) {
          const data = await res.json();
          setEstado(data.status); // ‚úÖ Actualiza el estado del pago
          setUltimaConsulta(new Date().toLocaleTimeString()); // üïì Guarda hora de √∫ltima consulta

          if (data.status !== "PENDING") {
            clearInterval(intervalo); // üõë Deja de consultar si ya no est√° pendiente
          }
        } else if (res.status === 404) {
          setEstado("NO_ENCONTRADO");
          clearInterval(intervalo); // üõë Si no existe, tambi√©n detenemos polling
        }
      } catch (error) {
        console.error("‚ùå Error consultando estado:", error);
      }
    };

    consultarEstado(); // ‚ñ∂Ô∏è Primera llamada inmediata

    intervalo = setInterval(consultarEstado, 3000); // üîÅ Polling cada 3 segundos

    return () => {
      if (intervalo) clearInterval(intervalo); // üßπ Limpia intervalo al desmontar componente
    };
  }, [reference]);

  // üé® Renderizado condicional basado en el estado
  const renderEstado = () => {
    switch (estado) {
      case "APPROVED":
        return <h2 style={{ color: "green" }}>‚úÖ ¬°Pago aprobado!</h2>;
      case "DECLINED":
        return <h2 style={{ color: "red" }}>‚ùå Pago rechazado</h2>;
      case "PENDING":
        return <h2 style={{ color: "orange" }}>‚è≥ Esperando confirmaci√≥n...</h2>;
      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>‚ö†Ô∏è Referencia no encontrada</h2>;
      default:
        return estado === null
          ? <h2>üîç Consultando estado...</h2>
          : <h2 style={{ color: "gray" }}>‚ö†Ô∏è Estado desconocido: {estado}</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>üßæ Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>

      {/* ‚ôø Accesibilidad: lector de pantalla escuchar√° cambios */}
      <div aria-live="polite">
        {renderEstado()}
      </div>

      {/* ‚è±Ô∏è Muestra la hora de la √∫ltima consulta */}
      {ultimaConsulta && (
        <p style={{ fontSize: "0.85rem", color: "#666", marginTop: "0.5rem" }}>
          √öltima consulta: {ultimaConsulta}
        </p>
      )}

      <button
        onClick={() => window.location.href = "/"}
        style={{ marginTop: "1.5rem", padding: "10px 20px", cursor: "pointer" }}
      >
        üîô Volver a la tienda
      </button>
    </div>
  );
};

export default EstadoPago;
