// üìå Importamos React y el contexto global
import React, { useContext } from "react";
import { CarritoContext } from "../context/CarritoContext"; // üìå Accedemos al estado global del carrito
import axios from "axios"; // üìå Importamos Axios para manejar la comunicaci√≥n con el backend

const Carrito = () => {
  const { carrito, setCarrito } = useContext(CarritoContext); // üìå Estado global del carrito

    // üî• NUEVO: Funci√≥n para calcular el total del carrito üî•
  const calcularTotal = () => {
    return carrito.reduce((total, producto) => total + producto.precio * producto.cantidad, 0);
  };

  // üìå Funci√≥n para eliminar productos del carrito
  const eliminarDelCarrito = (productoId) => {
    // üîÑ Filtramos el producto a eliminar y actualizamos el estado global
    const nuevoCarrito = carrito.filter((p) => p._id !== productoId);
    setCarrito(nuevoCarrito); 

    // üìå Eliminamos tambi√©n en el backend
    axios.delete(`http://localhost:3000/carrito/${productoId}`)
      .catch(err => console.error("‚ùå Error al eliminar producto:", err));
  };

  return (
    <div>
      <h2>üõí Carrito de Compras</h2>

      {carrito.length === 0 ? (
        <p>Tu carrito est√° vac√≠o.</p>
      ) : (
        carrito.map((producto) => (
          <div key={producto._id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px" }}>
            <h3>{producto.nombre}</h3>  {/* üìå Nombre del producto */}

            {/* üì∏ Imagen del producto */}
            <img src={producto.imagenUrl} alt={producto.nombre} width="100px" />

            <p><strong>Precio:</strong> ${producto.precio} USD</p>  {/* üí≤ Precio */}
            <p><strong>Cantidad:</strong> {producto.cantidad}</p>  {/* üî¢ Cantidad seleccionada */}

            {/* üìå Bot√≥n para eliminar productos del carrito */}
            <button onClick={() => eliminarDelCarrito(producto._id)}>‚ùå Eliminar</button>  
          </div>
        ))
      )}

          {/* üî• NUEVO: Mostramos el total del carrito üî• */}
          <h3>Total a pagar: ${calcularTotal().toFixed(2)} USD</h3>
    </div>
  );
};

export default Carrito;
