import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { CarritoContext } from "../context/CarritoContext"; // üìå Accedemos al estado global del carrito

const Pago = () => {
  const { carrito } = useContext(CarritoContext); // üìå Obtenemos el carrito del contexto
  const [email, setEmail] = useState("");
  const [total, setTotal] = useState(0); 
  const [error, setError] = useState(""); // üî• NUEVO: Estado para manejar errores
  const [loading, setLoading] = useState(false); // üî• NUEVO: Estado para manejar carga

  // üìå üî• Funci√≥n para calcular el total del carrito con validaci√≥n üî•
  const calcularTotal = () => {
    if (!carrito || carrito.length === 0) {
      console.warn("‚ùå El carrito est√° vac√≠o.");
      return 0;
    }
    const totalCalculado = carrito.reduce((total, producto) => total + producto.precio * producto.cantidad, 0);
    console.log("‚úÖ Total calculado:", totalCalculado);
    return totalCalculado;
  };

  // üìå üî• Actualizar el estado `total` cuando cambie el carrito üî•
  useEffect(() => {
    setTotal(calcularTotal());
  }, [carrito]);

  // üìå üî• Funci√≥n para procesar el pago con PSE üî•
  const pagarConPSE = async () => {
    setError(""); 
    setLoading(true); 
    console.log("üöÄ Procesando pago con datos:", { usuario: email, valor: total });

    if (!email) {
      setError("‚ùå Debes ingresar un correo electr√≥nico.");
      setLoading(false);
      return;
    }

    if (total === 0) {
      setError("‚ùå No puedes pagar un carrito vac√≠o.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post("http://localhost:3000/pago/pse", { usuario: email, valor: total });
      alert("‚úÖ Pago iniciado, sigue las instrucciones en PSE.");
      console.log("üéØ Respuesta de pago:", response.data);
    } catch (err) {
      setError("‚ùå Hubo un problema al procesar el pago. Intenta nuevamente.");
      console.error("‚ùå Error al procesar el pago:", err.response ? err.response.data : err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>üí≥ Pagar con PSE</h2>

      {/* üìå Campo para ingresar correo */}
      <input 
        type="email" 
        placeholder="Correo electr√≥nico" 
        onChange={(e) => setEmail(e.target.value)} 
        value={email} 
        required
      />

      {/* üî• NUEVO: Mostramos el total calculado antes de pagar üî• */}
      <h3>Total a pagar: ${total.toFixed(2)} COP</h3>

      {/* üî• NUEVO: Mensaje de error */}
      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

      {/* üî• NUEVO: Bot√≥n para pagar con PSE üî• */}
      <button 
        onClick={pagarConPSE} 
        style={{
          backgroundColor: loading ? "#ccc" : "#4CAF50", 
          color: "white", 
          padding: "10px", 
          border: "none", 
          cursor: loading ? "not-allowed" : "pointer"
        }}
        disabled={loading}
      >
        {loading ? "‚è≥ Procesando..." : "üí∞ Pagar ahora"}
      </button>
    </div>
  );
};

export default Pago;
