import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // 🧭 Para redirección después del pago
import { CarritoContext } from "../context/CarritoContext"; 

const Pago = () => {
  const { carrito } = useContext(CarritoContext);  
  const navigate = useNavigate();

  const [email, setEmail] = useState("");  
  const [nombre, setNombre] = useState("");  
  const [total, setTotal] = useState(0);  
  const [document, setDocument] = useState("");  
  const [documentType, setDocumentType] = useState("CC");  
  const [bankCode, setBankCode] = useState("");  
  const [bancos, setBancos] = useState([]);  
  const [error, setError] = useState("");  
  const [loading, setLoading] = useState(false);  

  // 🔹 Obtener lista de bancos desde backend
  useEffect(() => {
    axios.get("http://localhost:3000/bancos")
      .then(res => {
        console.log("✅ Bancos recibidos:", res.data); 
        if (Array.isArray(res.data)) {
          setBancos([
            { nombre: "Banco que aprueba (Sandbox PSE)", codigo: "1" },  
            { nombre: "Banco que rechaza (Sandbox PSE)", codigo: "2" },  
            ...res.data
          ]);
        } else {
          console.error("❌ Formato inválido en respuesta:", res.data);
          setBancos([]);
        }
      })
      .catch(err => {
        console.error("❌ Error obteniendo bancos:", err);
        setBancos([]);
      });
  }, []);

  // 🔹 Calcular total según carrito
  useEffect(() => {
    if (carrito?.length > 0) {
      setTotal(carrito.reduce((acc, prod) => acc + (prod.precio * prod.cantidad), 0));
    }
  }, [carrito]);

  // 🔎 Validación básica de email
  const validarEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  // 💳 Función para procesar el pago con PSE
  const pagarConPSE = async () => {
    setError("");  
    setLoading(true);  
    console.log("🚀 Código de institución antes de enviar:", bankCode);

    // 🔍 Validaciones previas
    if (!email || !validarEmail(email)) {
      setError("❌ Ingresa un correo electrónico válido.");
      setLoading(false);
      return;
    }

    if (!nombre || !document || !documentType || !bankCode || !["1", "2"].includes(bankCode)) {
      setError("❌ Selecciona un banco válido para pruebas Sandbox.");
      setLoading(false);
      return;
    }

    if (total === 0) {
      setError("❌ No puedes pagar un carrito vacío.");
      setLoading(false);
      return;
    }

    try {
      // 📨 Enviar al backend local (no directamente a Wompi)
      const response = await axios.post("http://localhost:3000/pago/pse", {
        usuario: email, 
        nombre,  
        valor: total, 
        document, 
        document_type: documentType, 
        financial_institution_code: String(bankCode)  
      });

      console.log("🎯 Respuesta del backend:", response.data);
      const { reference } = response.data;

      if (!reference) {
        throw new Error("⚠️ Backend no devolvió una referencia válida.");
      }

      alert("✅ Pago iniciado, sigue las instrucciones en PSE.");
      
      // 🧭 Redirección dinámica usando la referencia generada
      console.log("🚦 Redirigiendo a:", `/estado/${reference}`);
      navigate(`/estado/${reference}`);

    } catch (err) {
      setError("❌ Hubo un problema al procesar el pago. Intenta nuevamente.");
      console.error("❌ Error detallado:", err.response?.data || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>💳 Pagar con PSE</h2>

      {/* Formulario de datos */}
      <input type="text" placeholder="Nombre completo" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
      <input type="email" placeholder="Correo electrónico" value={email} onChange={(e) => setEmail(e.target.value)} required />
      <input type="text" placeholder="Número de documento" value={document} onChange={(e) => setDocument(e.target.value)} required />

      <select value={documentType} onChange={(e) => setDocumentType(e.target.value)}>
        <option value="CC">Cédula</option>
        <option value="CE">Cédula Extranjera</option>
        <option value="NIT">NIT</option>
      </select>

      <select value={bankCode} onChange={(e) => setBankCode(e.target.value)}>
        <option value="">Selecciona tu banco</option>
        {bancos.map((banco, index) => (
          <option key={`${banco.codigo}-${index}`} value={banco.codigo}>
            {banco.nombre}
          </option>
        ))}
      </select>

      <h3>Total a pagar: ${total.toFixed(2)} COP</h3>

      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

      <button 
        onClick={pagarConPSE}
        style={{
          backgroundColor: loading ? "#ccc" : "#4CAF50",
          color: "white",
          padding: "10px",
          border: "none",
          cursor: loading ? "not-allowed" : "pointer"
        }}
        disabled={loading}
      >
        {loading ? "⏳ Procesando..." : "💰 Pagar ahora"}
      </button>
    </div>
  );
};

export default Pago;
