import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { CarritoContext } from "../context/CarritoContext"; 

const Pago = () => {
  const { carrito } = useContext(CarritoContext);  
  const [email, setEmail] = useState("");  // üìå Estado para el email
  const [nombre, setNombre] = useState("");  // üìå Estado para el nombre del usuario
  const [total, setTotal] = useState(0);  // üìå Estado para el total de la compra
  const [document, setDocument] = useState("");  // üìå Estado para el documento de identidad
  const [documentType, setDocumentType] = useState("CC");  // üìå Estado para el tipo de documento
  const [bankCode, setBankCode] = useState("");  // üìå Estado para el c√≥digo del banco seleccionado
  const [bancos, setBancos] = useState([]);  // üìå Estado para almacenar la lista de bancos
  const [error, setError] = useState("");  // üìå Estado para manejar errores de validaci√≥n
  const [loading, setLoading] = useState(false);  // üìå Estado para indicar si el pago se est√° procesando

  // üîπ Obtener lista de bancos desde el backend al cargar la p√°gina
  useEffect(() => {
    axios.get("http://localhost:3000/bancos")
      .then(res => {
        console.log("‚úÖ Bancos recibidos:", res.data); 
        if (res.data && Array.isArray(res.data.bancos)) {  
          setBancos(res.data.bancos); // üìå Guardar la lista de bancos correctamente
        } else {
          console.error("‚ùå Formato incorrecto en la respuesta del backend.");
          setBancos([]);
        }
      })
      .catch(err => {
        console.error("‚ùå Error obteniendo bancos:", err);
        setBancos([]);
      });
  }, []);

  // üîπ Calcular el total de la compra basado en el carrito
  useEffect(() => {
    if (carrito && carrito.length > 0) {
      const totalCalculado = carrito.reduce((total, producto) => {
        if (!producto.precio || !producto.cantidad) {
          console.warn("‚ö†Ô∏è Producto con datos inv√°lidos:", producto);
          return total;
        }
        return total + producto.precio * producto.cantidad;
      }, 0);
      setTotal(totalCalculado);
    }
  }, [carrito]);

  // üîπ Validaci√≥n de formato de email
  const validarEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  // üìå Funci√≥n para iniciar el pago con PSE
  const pagarConPSE = async () => {
    setError("");  // üîπ Limpiar errores previos
    setLoading(true);  // üîπ Activar estado de carga

    console.log("üöÄ Procesando pago con datos:", { 
      usuario: email, 
      nombre, 
      valor: total, 
      document, 
      documentType, 
      bankCode 
    });

    // üîπ Validaciones antes de enviar la solicitud
    if (!email || !validarEmail(email)) {
      setError("‚ùå Ingresa un correo electr√≥nico v√°lido.");
      setLoading(false);
      return;
    }

    if (!nombre || !document || !documentType || !bankCode) {
      setError("‚ùå Todos los campos son obligatorios.");
      setLoading(false);
      return;
    }

    if (total === 0) {
      setError("‚ùå No puedes pagar un carrito vac√≠o.");
      setLoading(false);
      return;
    }

    try {
      // üîπ Convertir `bankCode` a `String` antes de enviarlo
      const response = await axios.post("http://localhost:3000/pago/pse", { 
        usuario: email, 
        nombre,  
        valor: total, 
        document, 
        document_type: documentType, 
        financial_institution_code: String(bankCode) // üìå Conversi√≥n a string
      });

      alert("‚úÖ Pago iniciado, sigue las instrucciones en PSE.");
      console.log("üéØ Respuesta de pago:", response.data);
    } catch (err) {
      setError("‚ùå Hubo un problema al procesar el pago. Intenta nuevamente.");
      console.error("‚ùå Error al procesar el pago:", err.response ? err.response.data : err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>üí≥ Pagar con PSE</h2>

      {/* üîπ Campos de ingreso de datos del usuario */}
      <input type="text" placeholder="Nombre completo" onChange={(e) => setNombre(e.target.value)} value={nombre} required />
      <input type="email" placeholder="Correo electr√≥nico" onChange={(e) => setEmail(e.target.value)} value={email} required />
      <input type="text" placeholder="N√∫mero de documento" onChange={(e) => setDocument(e.target.value)} value={document} required />
      
      {/* üîπ Dropdown de tipo de documento */}
      <select onChange={(e) => setDocumentType(e.target.value)} value={documentType}>
        <option value="CC">C√©dula</option>
        <option value="CE">C√©dula Extranjera</option>
        <option value="NIT">NIT</option>
      </select>

      {/* üîπ Dropdown de bancos con opciones de prueba en Sandbox */}
      <select onChange={(e) => setBankCode(e.target.value)} value={bankCode}>
        <option value="">Selecciona tu banco</option>
        <option value="1">Banco que aprueba (Sandbox PSE)</option>  
        <option value="2">Banco que rechaza (Sandbox PSE)</option>  
        {bancos.length > 0 ? bancos.map((banco) => (
          <option key={banco.codigo} value={banco.codigo}>{banco.nombre}</option>
        )) : <option disabled>No se encontraron bancos.</option>}
      </select>

      <h3>Total a pagar: ${total.toFixed(2)} COP</h3>

      {/* üîπ Mostrar errores en pantalla si hay alguno */}
      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

      {/* üîπ Bot√≥n de pago con estado de carga */}
      <button 
        onClick={pagarConPSE} 
        style={{
          backgroundColor: loading ? "#ccc" : "#4CAF50", 
          color: "white", 
          padding: "10px", 
          border: "none", 
          cursor: loading ? "not-allowed" : "pointer"
        }}
        disabled={loading}
      >
        {loading ? "‚è≥ Procesando..." : "üí∞ Pagar ahora"}
      </button>
    </div>
  );
};

export default Pago;
