import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const EstadoPago = () => {
  const { reference } = useParams(); // 📦 Extrae referencia de la URL
  const [estado, setEstado] = useState(null); // 🔁 Estado del pago: PENDING, APPROVED, etc.
  const [ultimaConsulta, setUltimaConsulta] = useState(null); // 🕓 Última hora de consulta

  useEffect(() => {
    let intervalo;

    const consultarEstado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);

        if (res.ok) {
          const data = await res.json();
          setEstado(data.status); // ✅ Actualizamos el estado
          setUltimaConsulta(new Date().toLocaleTimeString()); // ⏱️ Guardamos hora local

          if (data.status !== "PENDING") {
            clearInterval(intervalo); // 🛑 Detenemos polling si ya tenemos resultado
          }
        } else if (res.status === 404) {
          setEstado("NO_ENCONTRADO");
          clearInterval(intervalo);
        }
      } catch (error) {
        console.error("❌ Error consultando estado:", error);
      }
    };

    consultarEstado(); // ▶️ Hacemos la primera consulta inmediata
    intervalo = setInterval(consultarEstado, 3000); // 🔁 Repetimos cada 3 segundos

    return () => {
      if (intervalo) clearInterval(intervalo); // 🧼 Limpiamos intervalo al desmontar
    };
  }, [reference]);

  // ✅ Renderizado condicional según estado
  const renderEstado = () => {
    switch (estado) {
      case "APPROVED":
        return (
          <div>
            <h2 style={{ color: "green" }}>🎉 ¡Gracias por tu compra!</h2>
            <p>Tu pago fue aprobado exitosamente.</p>
            <p>Te redirigiremos pronto o puedes volver a la tienda ahora mismo.</p>
          </div>
        );
      case "DECLINED":
        return <h2 style={{ color: "red" }}>❌ Pago rechazado</h2>;
      case "PENDING":
        return <h2 style={{ color: "orange" }}>⏳ Esperando confirmación...</h2>;
      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>⚠️ Referencia no encontrada</h2>;
      default:
        return estado === null
          ? <h2>🔍 Consultando estado...</h2>
          : <h2 style={{ color: "gray" }}>⚠️ Estado desconocido: {estado}</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>🧾 Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>

      {/* 📢 Renderizamos el estado dinámico */}
      <div aria-live="polite">
        {renderEstado()}
      </div>

      {/* 🕓 Mostramos hora de última consulta */}
      {ultimaConsulta && (
        <p style={{ fontSize: "0.85rem", color: "#666", marginTop: "0.5rem" }}>
          Última consulta: {ultimaConsulta}
        </p>
      )}

      <button
        onClick={() => window.location.href = "/"}
        style={{ marginTop: "1.5rem", padding: "10px 20px", cursor: "pointer" }}
      >
        🔙 Volver a la tienda
      </button>
    </div>
  );
};

export default EstadoPago;
