import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

// üì• Importamos el componente de resumen del pago (aseg√∫rate que la ruta sea correcta)
import ResumenPago from "./ResumenPago"; // ajusta la ruta si est√° en otra carpeta

const EstadoPago = () => {
  const { reference } = useParams(); // üì¶ Capturamos la referencia desde la URL
  const [estado, setEstado] = useState(null); // üîÅ Estado actual del pago
  const [ultimaConsulta, setUltimaConsulta] = useState(null); // üïì Timestamp del √∫ltimo polling
  const [errorConsulta, setErrorConsulta] = useState(false); // ‚ö†Ô∏è Si ocurre un error en la consulta

  useEffect(() => {
    let intervalo;
    let delayInicial;

    // üîç Funci√≥n que consulta al backend por el estado del pago
    const consultarEstado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);
        if (!res.ok) {
          if (res.status === 404) setEstado("NO_ENCONTRADO");
          else setErrorConsulta(true);
          clearInterval(intervalo);
          return;
        }

        const data = await res.json();
        setEstado(data.status);
        setUltimaConsulta(new Date().toLocaleTimeString());
        if (data.status !== "PENDING") clearInterval(intervalo);
      } catch (error) {
        console.error("‚ùå Error en la consulta:", error);
        setErrorConsulta(true);
        clearInterval(intervalo);
      }
    };

    // ‚è±Ô∏è Iniciamos la primera consulta con un delay para evitar conflictos con el guardado en MongoDB
    delayInicial = setTimeout(() => {
      consultarEstado();
      intervalo = setInterval(consultarEstado, 3000); // üîÅ Polling cada 3 segundos
    }, 1500);

    // üßº Cleanup al desmontar el componente
    return () => {
      clearInterval(intervalo);
      clearTimeout(delayInicial);
    };
  }, [reference]);

  // üé® Visualizaci√≥n seg√∫n estado del pago
  const renderEstado = () => {
    if (errorConsulta) {
      return <h2 style={{ color: "crimson" }}>üö® Error al consultar el estado del pago</h2>;
    }

    switch (estado) {
      case "APPROVED":
        return (
          <div>
            <h2 style={{ color: "green" }}>üéâ ¬°Pago aprobado!</h2>
            <p>Gracias por tu compra.</p>
            {/* üßæ Resumen detallado de la transacci√≥n */}
            <ResumenPago reference={reference} />
          </div>
        );

      case "DECLINED":
        return (
          <div>
            <h2 style={{ color: "red" }}>‚ùå Transacci√≥n rechazada</h2>
            <p>Por favor intenta nuevamente con otro banco.</p>
          </div>
        );

      case "PENDING":
        return <h2 style={{ color: "orange" }}>‚è≥ Esperando confirmaci√≥n del banco‚Ä¶</h2>;

      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>‚ö†Ô∏è Referencia no encontrada</h2>;

      case null:
        return <h2>üîç Consultando estado del pago‚Ä¶</h2>;

      default:
        return <h2 style={{ color: "gray" }}>üìå Estado desconocido: {estado}</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>üßæ Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>

      {/* ‚ôø Zona que se actualiza din√°micamente */}
      <div aria-live="polite" role="status">
        {renderEstado()}
      </div>

      {/* üïì Timestamp √∫til para debug o UX */}
      {ultimaConsulta && (
        <p style={{ fontSize: "0.85rem", color: "#666", marginTop: "0.5rem" }}>
          √öltima verificaci√≥n: {ultimaConsulta}
        </p>
      )}

      {/* üîÅ Acci√≥n para volver a la tienda */}
      <button
        onClick={() => window.location.href = "/"}
        style={{
          marginTop: "1.5rem",
          padding: "10px 20px",
          cursor: "pointer"
        }}
      >
        üîô Volver a la tienda
      </button>
    </div>
  );
};

export default EstadoPago;
