import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { CarritoContext } from "../context/CarritoContext";

const Pago = () => {
  const { carrito } = useContext(CarritoContext);

  // üßæ Datos del formulario
  const [email, setEmail] = useState("");
  const [nombre, setNombre] = useState("");
  const [total, setTotal] = useState(0);
  const [document, setDocument] = useState("");
  const [documentType, setDocumentType] = useState("CC");
  const [bankCode, setBankCode] = useState("");
  const [bancos, setBancos] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [mensaje, setMensaje] = useState("");

  // üè¶ Obtener lista de bancos desde el backend
  useEffect(() => {
    axios.get("http://localhost:3000/bancos")
      .then(res => {
        if (Array.isArray(res.data)) {
          setBancos([
            { nombre: "Banco que aprueba (Sandbox)", codigo: "1" },
            { nombre: "Banco que rechaza (Sandbox)", codigo: "2" },
            ...res.data // üîó bancos reales para producci√≥n
          ]);
        }
      })
      .catch(err => {
        console.error("‚ùå Error al obtener bancos:", err);
        setBancos([]);
      });
  }, []);

  // üí∞ Calcular total del carrito
  useEffect(() => {
    if (carrito?.length > 0) {
      const totalCalculado = carrito.reduce((acc, p) => acc + p.precio * p.cantidad, 0);
      setTotal(totalCalculado);
    }
  }, [carrito]);

  const validarEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  // üí≥ Iniciar transacci√≥n PSE
  const pagarConPSE = async () => {
    setError("");
    setMensaje("");
    setLoading(true);

    // ‚ö†Ô∏è Validaciones previas
    if (!email || !validarEmail(email)) {
      setError("‚ùå Ingresa un correo electr√≥nico v√°lido.");
      setLoading(false);
      return;
    }

    if (!nombre || !document || !documentType || !bankCode) {
      setError("‚ùå Completa todos los campos correctamente.");
      setLoading(false);
      return;
    }

    if (total < 1500) {
      setError("‚ùå Monto m√≠nimo permitido: $1.500 COP.");
      setLoading(false);
      return;
    }

    try {
      const bancoSeleccionado = bancos.find(b => b.codigo === bankCode);

      // üì¶ Armar payload antes de enviarlo
      const payload = {
        usuario: email,
        nombre_cliente: nombre,
        banco_nombre: bancoSeleccionado?.nombre || "Desconocido",
        valor: total,
        document,
        document_type: documentType,
        financial_institution_code: String(bankCode)
      };

      // üß™ Log para verificar el contenido del payload
      console.log("üì¶ Payload enviado a backend:", JSON.stringify(payload, null, 2));

      // üì§ Enviar al backend
      const response = await axios.post("http://localhost:3000/pago/pse", payload);

      const { reference, redirect_url } = response.data;

      if (!redirect_url) {
        throw new Error("‚ö†Ô∏è No se recibi√≥ URL de redirecci√≥n desde el backend.");
      }

      console.log("üîÅ Redirigiendo a Wompi:", redirect_url);
      setMensaje("‚úÖ Redirigi√©ndote a Wompi...");
      window.location.href = redirect_url;

    } catch (err) {
      console.error("‚ùå Error en pago:", err.response?.data || err.message);
      setError("‚ùå No se pudo procesar el pago. Intenta nuevamente.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>üí≥ Pagar con PSE</h2>

      <input
        type="text"
        placeholder="Nombre completo"
        value={nombre}
        onChange={(e) => setNombre(e.target.value)}
      />

      <input
        type="email"
        placeholder="Correo electr√≥nico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />

      <input
        type="text"
        placeholder="N√∫mero de documento"
        value={document}
        onChange={(e) => setDocument(e.target.value)}
      />

      <select value={documentType} onChange={(e) => setDocumentType(e.target.value)}>
        <option value="CC">C√©dula</option>
        <option value="CE">C√©dula Extranjera</option>
        <option value="TI">Tarjeta de Identidad</option>
        <option value="NIT">NIT</option>
      </select>

      <select value={bankCode} onChange={(e) => setBankCode(e.target.value)}>
        <option value="">Selecciona tu banco</option>
        {bancos.map((banco, index) => (
          <option key={`${banco.codigo}-${index}`} value={banco.codigo}>
            {banco.nombre}
          </option>
        ))}
      </select>

      <h3>Total a pagar: ${total.toFixed(2)} COP</h3>

      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}
      {mensaje && <p style={{ color: "green", fontWeight: "bold" }}>{mensaje}</p>}

      <button
        onClick={pagarConPSE}
        style={{
          backgroundColor: loading ? "#ccc" : "#4CAF50",
          color: "white",
          padding: "10px",
          border: "none",
          cursor: loading ? "not-allowed" : "pointer"
        }}
        disabled={loading}
      >
        {loading ? "‚è≥ Procesando..." : "üí∞ Pagar ahora"}
      </button>
    </div>
  );
};

export default Pago;
