import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { CarritoContext } from "../context/CarritoContext"; 

const Pago = () => {
  const { carrito } = useContext(CarritoContext);  
  const [email, setEmail] = useState("");  
  const [nombre, setNombre] = useState("");  
  const [total, setTotal] = useState(0);  
  const [document, setDocument] = useState("");  
  const [documentType, setDocumentType] = useState("CC");  
  const [bankCode, setBankCode] = useState("");  
  const [bancos, setBancos] = useState([]);  
  const [error, setError] = useState("");  
  const [loading, setLoading] = useState(false);  

  // üîπ Obtener lista de bancos desde el backend y agregar opciones de prueba
  useEffect(() => {
    axios.get("http://localhost:3000/bancos")
      .then(res => {
        console.log("‚úÖ Bancos recibidos:", res.data); 

        if (Array.isArray(res.data)) {  
          setBancos([
            { nombre: "Banco que aprueba (Sandbox PSE)", codigo: "1" },  
            { nombre: "Banco que rechaza (Sandbox PSE)", codigo: "2" },  
            ...res.data  
          ]);
        } else {
          console.error("‚ùå Formato incorrecto en la respuesta del backend:", res.data);
          setBancos([]);
        }
      })
      .catch(err => {
        console.error("‚ùå Error obteniendo bancos:", err);
        setBancos([]);
      });
  }, []);

  // üîπ Calcular el total de la compra basado en el carrito
  useEffect(() => {
    if (carrito?.length > 0) {
      setTotal(carrito.reduce((total, producto) => total + (producto.precio * producto.cantidad), 0));
    }
  }, [carrito]);

  // üîπ Validaci√≥n de email
  const validarEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  // üìå Funci√≥n para iniciar el pago con PSE
  const pagarConPSE = async () => {
    setError("");  
    setLoading(true);  

    console.log("üöÄ C√≥digo de instituci√≥n antes de enviar:", bankCode);

    // üîπ Validaciones antes de enviar la solicitud
    if (!email || !validarEmail(email)) {
      setError("‚ùå Ingresa un correo electr√≥nico v√°lido.");
      setLoading(false);
      return;
    }

    if (!nombre || !document || !documentType || !bankCode || (bankCode !== "1" && bankCode !== "2")) {
      setError("‚ùå Selecciona un banco v√°lido para la prueba en Sandbox.");
      setLoading(false);
      return;
    }

    if (total === 0) {
      setError("‚ùå No puedes pagar un carrito vac√≠o.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post("http://localhost:3000/pago/pse", { 
        usuario: email, 
        nombre,  
        valor: total, 
        document, 
        document_type: documentType, 
        financial_institution_code: String(bankCode)  
      });

      alert("‚úÖ Pago iniciado, sigue las instrucciones en PSE.");
      console.log("üéØ Respuesta de pago:", response.data);
    } catch (err) {
      setError("‚ùå Hubo un problema al procesar el pago. Intenta nuevamente.");
      console.error("‚ùå Error detallado:", err.response?.data || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>üí≥ Pagar con PSE</h2>

      {/* üîπ Campos de usuario */}
      <input type="text" placeholder="Nombre completo" onChange={(e) => setNombre(e.target.value)} value={nombre} required />
      <input type="email" placeholder="Correo electr√≥nico" onChange={(e) => setEmail(e.target.value)} value={email} required />
      <input type="text" placeholder="N√∫mero de documento" onChange={(e) => setDocument(e.target.value)} value={document} required />
      
      {/* üîπ Dropdown de tipo de documento */}
      <select onChange={(e) => setDocumentType(e.target.value)} value={documentType}>
        <option value="CC">C√©dula</option>
        <option value="CE">C√©dula Extranjera</option>
        <option value="NIT">NIT</option>
      </select>

      {/* üîπ Dropdown de bancos con opciones de prueba en Sandbox */}
      <select onChange={(e) => setBankCode(e.target.value)} value={bankCode}>
        <option value="">Selecciona tu banco</option>
        {bancos.map((banco) => (
          <option key={banco.codigo} value={banco.codigo}>{banco.nombre}</option>
        ))}
      </select>

      <h3>Total a pagar: ${total.toFixed(2)} COP</h3>

      {/* üîπ Mostrar errores en pantalla */}
      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

      {/* üîπ Bot√≥n de pago con estado de carga */}
      <button 
        onClick={pagarConPSE} 
        style={{
          backgroundColor: loading ? "#ccc" : "#4CAF50", 
          color: "white", 
          padding: "10px", 
          border: "none", 
          cursor: loading ? "not-allowed" : "pointer"
        }}
        disabled={loading}
      >
        {loading ? "‚è≥ Procesando..." : "üí∞ Pagar ahora"}
      </button>
    </div>
  );
};

export default Pago;
