import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const EstadoPago = () => {
  const { reference } = useParams();
  const [estado, setEstado] = useState(null);

  useEffect(() => {
    const intervalo = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);
        if (res.status === 200) {
          const data = await res.json();
          setEstado(data.status);
          if (data.status !== "PENDING") clearInterval(intervalo);
        } else if (res.status === 404) {
          setEstado("NO_ENCONTRADO");
          clearInterval(intervalo);
        }
      } catch (error) {
        console.error("‚ùå Error consultando estado:", error);
      }
    }, 3000);
    return () => clearInterval(intervalo);
  }, [reference]);

  const renderEstado = () => {
    switch (estado) {
      case "APPROVED":
        return <h2 style={{ color: "green" }}>‚úÖ ¬°Pago aprobado!</h2>;
      case "DECLINED":
        return <h2 style={{ color: "red" }}>‚ùå Pago rechazado</h2>;
      case "PENDING":
        return <h2 style={{ color: "orange" }}>‚è≥ Esperando confirmaci√≥n...</h2>;
      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>‚ö†Ô∏è Referencia no encontrada</h2>;
      default:
        return <h2>üîç Consultando estado...</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>üßæ Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>
      {renderEstado()}
      <button onClick={() => window.location.href = "/"} style={{ marginTop: "1rem" }}>
        üîô Volver a la tienda
      </button>
    </div>
  );
};

export default EstadoPago;
