import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { CarritoContext } from "../context/CarritoContext"; 

const Pago = () => {
  const { carrito } = useContext(CarritoContext);  
  const [email, setEmail] = useState("");
  const [total, setTotal] = useState(0);  
  const [document, setDocument] = useState("");  
  const [documentType, setDocumentType] = useState("CC");  
  const [bankCode, setBankCode] = useState("000");  
  const [error, setError] = useState("");  
  const [loading, setLoading] = useState(false);  

  // üìå üî• Funci√≥n para calcular el total del carrito con validaci√≥n üî•
  const calcularTotal = () => {
    if (!carrito || carrito.length === 0) {
      console.warn("‚ùå El carrito est√° vac√≠o.");
      return 0;
    }
    const totalCalculado = carrito.reduce((total, producto) => total + producto.precio * producto.cantidad, 0);
    console.log("‚úÖ Total calculado:", totalCalculado);
    return totalCalculado;
  };

  useEffect(() => {
    setTotal(calcularTotal());
  }, [carrito]);

  // üìå üî• Funci√≥n para procesar el pago con PSE üî•
  const pagarConPSE = async () => {
    setError(""); 
    setLoading(true); 
    console.log("üöÄ Procesando pago con datos:", { usuario: email, valor: total, document, documentType, bankCode });

    if (!email || !document || !documentType || !bankCode) {
      setError("‚ùå Todos los campos son obligatorios.");
      setLoading(false);
      return;
    }

    if (total === 0) {
      setError("‚ùå No puedes pagar un carrito vac√≠o.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post("http://localhost:3000/pago/pse", { 
        usuario: email, 
        valor: total, 
        document, 
        document_type: documentType, 
        financial_institution_code: bankCode 
      });

      alert("‚úÖ Pago iniciado, sigue las instrucciones en PSE.");
      console.log("üéØ Respuesta de pago:", response.data);
    } catch (err) {
      setError("‚ùå Hubo un problema al procesar el pago. Intenta nuevamente.");
      console.error("‚ùå Error al procesar el pago:", err.response ? err.response.data : err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>üí≥ Pagar con PSE</h2>

      {/* üìå Campos requeridos para el pago */}
      <input type="email" placeholder="Correo electr√≥nico" onChange={(e) => setEmail(e.target.value)} value={email} required />
      <input type="text" placeholder="N√∫mero de documento" onChange={(e) => setDocument(e.target.value)} value={document} required />
      
      <select onChange={(e) => setDocumentType(e.target.value)} value={documentType}>
        <option value="CC">C√©dula</option>
        <option value="CE">C√©dula Extranjera</option>
        <option value="NIT">NIT</option>
      </select>
      
      <select onChange={(e) => setBankCode(e.target.value)} value={bankCode}>
        <option value="000">Banco Ejemplo</option>
        <option value="001">Otro Banco</option>
      </select>

      {/* üî• Mostramos el total calculado antes de pagar üî• */}
      <h3>Total a pagar: ${total.toFixed(2)} COP</h3>

      {/* üî• Mensaje de error */}
      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

      {/* üî• Bot√≥n para pagar con PSE üî• */}
      <button 
        onClick={pagarConPSE} 
        style={{
          backgroundColor: loading ? "#ccc" : "#4CAF50", 
          color: "white", 
          padding: "10px", 
          border: "none", 
          cursor: loading ? "not-allowed" : "pointer"
        }}
        disabled={loading}
      >
        {loading ? "‚è≥ Procesando..." : "üí∞ Pagar ahora"}
      </button>
    </div>
  );
};

export default Pago;
