import React, { useEffect, useState } from "react";

const ResumenPago = ({ reference }) => {
  const [detalle, setDetalle] = useState(null);       // üì¶ Datos del pago completo
  const [error, setError] = useState(false);          // üö® Control de error en la consulta
  const [cargando, setCargando] = useState(true);     // ‚è≥ Muestra spinner si la consulta tarda

  useEffect(() => {
    const obtenerDetalle = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);
        if (!res.ok) throw new Error("Error al obtener detalle");

        const data = await res.json();
        setDetalle(data);       // ‚úÖ Almacenamos los datos de la transacci√≥n
      } catch (err) {
        console.error("‚ùå Error obteniendo resumen:", err);
        setError(true);         // üö® Mostramos error si algo falla
      } finally {
        setCargando(false);     // ‚úÖ Finaliza carga sin importar el resultado
      }
    };

    obtenerDetalle(); // üöÄ Llamamos a la funci√≥n apenas el componente se monta
  }, [reference]);

  // üé® Manejamos tres estados visuales: cargando, error o resumen listo
  if (cargando) return <p>‚è≥ Cargando detalles del pago...</p>;
  if (error || !detalle) return <p style={{ color: "crimson" }}>üö® No se pudo cargar el resumen del pago.</p>;

  return (
    <div style={{
      marginTop: "2rem",
      border: "1px solid #ccc",
      padding: "1rem",
      borderRadius: "8px",
      backgroundColor: "#f8f8f8",
      maxWidth: "400px",
      marginLeft: "auto",
      marginRight: "auto"
    }}>
      <h3 style={{ marginBottom: "1rem" }}>üìã Resumen de la Transacci√≥n</h3>

      <p><strong>Referencia:</strong> {detalle.reference}</p>
      <p><strong>Estado:</strong> {detalle.status}</p>

      <p>
        <strong>Monto:</strong>{" "}
        {detalle.amount_in_cents
          ? `$ ${(detalle.amount_in_cents / 100).toLocaleString("es-CO", { minimumFractionDigits: 2 })} COP`
          : "No disponible"}
      </p>

      <p>
        <strong>Fecha:</strong>{" "}
        {detalle.createdAt
          ? new Date(detalle.createdAt).toLocaleString("es-CO")
          : "No registrada"}
      </p>

      {/* üß† Campos adicionales que puedes activar si los almacenas en Mongo */}
      {/* <p><strong>Banco:</strong> {detalle.bank_name || "No disponible"}</p> */}
      {/* <p><strong>M√©todo de pago:</strong> {detalle.payment_method_type || "Desconocido"}</p> */}
    </div>
  );
};

export default ResumenPago;
