// üåê Importamos React y herramientas de routing
import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// üß© Importamos componentes de la app
import Productos from "./components/Productos";
import Carrito from "./components/Carrito";
import Pago from "./components/Pago";
import EstadoPago from "./components/EstadoPago";

// üåç Contexto global para el carrito
import { CarritoProvider } from "./context/CarritoContext";

// üé® Importamos estilos globales (aseg√∫rate de que App.css existe)
import "./App.css";

const App = () => {
  return (
    <CarritoProvider>
      <Router>
        <Routes>
          {/* üè† Ruta principal: cat√°logo, carrito y pago */}
          <Route
            path="/"
            element={
              <div>
                {/* üëá Eliminamos el h1 aqu√≠ porque el t√≠tulo ya est√° incluido en <Productos /> */}
                <Productos />
                <Carrito usuarioId={"123456"} />
                <Pago />
              </div>
            }
          />

          {/* üìÑ Ruta din√°mica para mostrar estado del pago */}
          <Route path="/estado/:reference" element={<EstadoPago />} />
        </Routes>
      </Router>
    </CarritoProvider>
  );
};

export default App;
