import React from "react";

// üß≠ Enrutador para controlar vistas seg√∫n URL
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// üß© Componentes de tu tienda
import Productos from "./components/Productos";
import Carrito from "./components/Carrito";
import Pago from "./components/Pago";
import EstadoPago from "./components/EstadoPago"; // üÜï Componente para estado del pago

// üåê Contexto global del carrito
import { CarritoProvider } from "./context/CarritoContext";

const App = () => {
  return (
    // üì¶ Proveedor de contexto: permite acceder al carrito desde cualquier componente
    <CarritoProvider>
      {/* üåê Router permite que la app tenga rutas din√°micas */}
      <Router>
        <Routes>
          {/* üè† P√°gina principal de la tienda */}
          <Route
            path="/"
            element={
              <div>
                <h1>Bienvenido a la tienda üöÄ</h1>
                <Productos />
                <Carrito usuarioId={"123456"} />
                <Pago />
              </div>
            }
          />

          {/* üßæ Vista din√°mica para mostrar el estado de pago */}
          <Route path="/estado/:reference" element={<EstadoPago />} />
        </Routes>
      </Router>
    </CarritoProvider>
  );
};

export default App;
