import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const EstadoPago = () => {
  const { reference } = useParams(); // üì¶ Referencia del pago desde la URL
  const [estado, setEstado] = useState(null); // üîÑ Estado din√°mico: APPROVED, DECLINED, etc.

  useEffect(() => {
    let intervalo; // ‚è±Ô∏è Referencia al setInterval para poder limpiarlo

    const consultarEstado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);
        if (res.status === 200) {
          const data = await res.json();
          setEstado(data.status);
          if (data.status !== "PENDING") clearInterval(intervalo); // üõë Detiene polling si ya no est√° pendiente
        } else if (res.status === 404) {
          setEstado("NO_ENCONTRADO");
          clearInterval(intervalo); // üõë Si no existe, deja de consultar
        }
      } catch (error) {
        console.error("‚ùå Error consultando estado:", error);
      }
    };

    consultarEstado(); // ‚úÖ Ejecuta inmediatamente al cargar

    intervalo = setInterval(consultarEstado, 3000); // üîÅ Consulta cada 3 segundos

    return () => clearInterval(intervalo); // üßπ Limpieza al desmontar
  }, [reference]);

  // üé® Mapeo del estado a un mensaje visual
  const renderEstado = () => {
    switch (estado) {
      case "APPROVED":
        return <h2 style={{ color: "green" }}>‚úÖ ¬°Pago aprobado!</h2>;
      case "DECLINED":
        return <h2 style={{ color: "red" }}>‚ùå Pago rechazado</h2>;
      case "PENDING":
        return <h2 style={{ color: "orange" }}>‚è≥ Esperando confirmaci√≥n...</h2>;
      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>‚ö†Ô∏è Referencia no encontrada</h2>;
      default:
        return <h2>üîç Consultando estado...</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>üßæ Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>
      
      {/* ‚ôø Accesibilidad: los lectores de pantalla ser√°n notificados del cambio */}
      <div aria-live="polite">
        {renderEstado()}
      </div>

      <button 
        onClick={() => window.location.href = "/"} 
        style={{ marginTop: "1rem", padding: "10px 20px", cursor: "pointer" }}
      >
        üîô Volver a la tienda
      </button>
    </div>
  );
};

export default EstadoPago;
