import React, { useEffect, useState } from "react";
import axios from "axios";

// üìå Componente del carrito que obtiene los productos desde el backend
const Carrito = ({ usuarioId }) => {
  const [carrito, setCarrito] = useState([]);

  // ‚úÖ Carga el carrito del usuario al montar el componente
  useEffect(() => {
    axios.get(`http://localhost:3000/carrito/${usuarioId}`)
      .then(res => setCarrito(res.data.productos))
      .catch(err => console.error("‚ùå Error al obtener el carrito:", err));
  }, [usuarioId]);

  return (
    <div>
      <h2>üõí Carrito de Compras</h2>
      {carrito.length === 0 ? (
        <p>Tu carrito est√° vac√≠o.</p>
      ) : (
        carrito.map((producto) => (
          <div key={producto.productoId._id}>
            <h3>{producto.productoId.nombre}</h3>
            <p>Precio: ${producto.productoId.precio}</p>
            <p>Cantidad: {producto.cantidad}</p>
            <button onClick={() => eliminarDelCarrito(producto.productoId._id)}>‚ùå Eliminar</button>
          </div>
        ))
      )}
    </div>
  );
};

// üìå Funci√≥n para eliminar productos del carrito
const eliminarDelCarrito = (productoId) => {
  axios.delete(`http://localhost:3000/carrito/${productoId}`)
    .then(() => console.log("‚úÖ Producto eliminado"))
    .catch(err => console.error("‚ùå Error al eliminar producto:", err));
};

export default Carrito;
