import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const EstadoPago = () => {
  const { reference } = useParams(); // üì¶ Obtenemos la referencia desde la URL din√°mica
  const [estado, setEstado] = useState(null); // üîÅ Guarda el estado del pago (APPROVED, DECLINED, etc.)
  const [ultimaConsulta, setUltimaConsulta] = useState(null); // üïì √öltima vez que hicimos polling
  const [errorConsulta, setErrorConsulta] = useState(false); // üö® Captura errores en fetch

  useEffect(() => {
    let intervalo;
    let delayInicial;

    // üîç Funci√≥n que consulta el estado del pago desde el backend
    const consultarEstado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);
        if (!res.ok) {
          if (res.status === 404) setEstado("NO_ENCONTRADO");
          else setErrorConsulta(true);
          clearInterval(intervalo); // ‚ö†Ô∏è Detenemos polling si hay error
          return;
        }

        const data = await res.json();
        setEstado(data.status); // ‚úÖ Actualizamos estado
        setUltimaConsulta(new Date().toLocaleTimeString()); // üïì Registramos el timestamp
        if (data.status !== "PENDING") clearInterval(intervalo); // üõë Detiene polling si hay respuesta definitiva
      } catch (err) {
        console.error("‚ùå Error en la consulta:", err);
        setErrorConsulta(true);
        clearInterval(intervalo);
      }
    };

    // ‚è±Ô∏è Primera consulta con delay de 1.5s para dar tiempo a MongoDB
    delayInicial = setTimeout(() => {
      consultarEstado();
      intervalo = setInterval(consultarEstado, 3000); // üîÅ Polling cada 3 segundos
    }, 1500);

    // üßº Limpiamos los intervalos si el componente se desmonta
    return () => {
      clearInterval(intervalo);
      clearTimeout(delayInicial);
    };
  }, [reference]);

  // üé® Renderiza el mensaje visual seg√∫n el estado actual
  const renderEstado = () => {
    if (errorConsulta)
      return <h2 style={{ color: "crimson" }}>üö® Error al obtener el estado. Intenta m√°s tarde.</h2>;

    switch (estado) {
      case "APPROVED":
        return (
          <div>
            <h2 style={{ color: "green" }}>üéâ ¬°Pago aprobado!</h2>
            <p>Gracias por tu compra. Pronto recibir√°s confirmaci√≥n.</p>
          </div>
        );
      case "DECLINED":
        return (
          <div>
            <h2 style={{ color: "red" }}>‚ùå Transacci√≥n rechazada</h2>
            <p>Por favor intenta con otro banco o m√©todo.</p>
          </div>
        );
      case "PENDING":
        return <h2 style={{ color: "orange" }}>‚è≥ Esperando confirmaci√≥n...</h2>;
      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>‚ö†Ô∏è Referencia no encontrada</h2>;
      default:
        return estado === null
          ? <h2>üîç Consultando estado del pago‚Ä¶</h2>
          : <h2 style={{ color: "gray" }}>üìå Estado desconocido: {estado}</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>üßæ Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>

      {/* ‚ôø Accesibilidad: permite que lectores de pantalla detecten cambios */}
      <div aria-live="polite" role="status">
        {renderEstado()}
      </div>

      {/* ‚è±Ô∏è Info √∫til para debug o mostrar tiempo al usuario */}
      {ultimaConsulta && (
        <p style={{ fontSize: "0.85rem", color: "#666", marginTop: "0.5rem" }}>
          √öltima verificaci√≥n: {ultimaConsulta}
        </p>
      )}

      <button
        onClick={() => window.location.href = "/"}
        style={{ marginTop: "1.5rem", padding: "10px 20px", cursor: "pointer" }}
      >
        üîô Volver a la tienda
      </button>
    </div>
  );
};

export default EstadoPago;
