import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const EstadoPago = () => {
  const { reference } = useParams(); // 📦 Obtenemos la referencia desde la URL dinámica
  const [estado, setEstado] = useState(null); // 🔁 Guarda el estado del pago (APPROVED, DECLINED, etc.)
  const [ultimaConsulta, setUltimaConsulta] = useState(null); // 🕓 Última vez que hicimos polling
  const [errorConsulta, setErrorConsulta] = useState(false); // 🚨 Captura errores en fetch

  useEffect(() => {
    let intervalo;
    let delayInicial;

    // 🔍 Función que consulta el estado del pago desde el backend
    const consultarEstado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);
        if (!res.ok) {
          if (res.status === 404) setEstado("NO_ENCONTRADO");
          else setErrorConsulta(true);
          clearInterval(intervalo); // ⚠️ Detenemos polling si hay error
          return;
        }

        const data = await res.json();
        setEstado(data.status); // ✅ Actualizamos estado
        setUltimaConsulta(new Date().toLocaleTimeString()); // 🕓 Registramos el timestamp
        if (data.status !== "PENDING") clearInterval(intervalo); // 🛑 Detiene polling si hay respuesta definitiva
      } catch (err) {
        console.error("❌ Error en la consulta:", err);
        setErrorConsulta(true);
        clearInterval(intervalo);
      }
    };

    // ⏱️ Primera consulta con delay de 1.5s para dar tiempo a MongoDB
    delayInicial = setTimeout(() => {
      consultarEstado();
      intervalo = setInterval(consultarEstado, 3000); // 🔁 Polling cada 3 segundos
    }, 1500);

    // 🧼 Limpiamos los intervalos si el componente se desmonta
    return () => {
      clearInterval(intervalo);
      clearTimeout(delayInicial);
    };
  }, [reference]);

  // 🎨 Renderiza el mensaje visual según el estado actual
  const renderEstado = () => {
    if (errorConsulta)
      return <h2 style={{ color: "crimson" }}>🚨 Error al obtener el estado. Intenta más tarde.</h2>;

    switch (estado) {
      case "APPROVED":
        return (
          <div>
            <h2 style={{ color: "green" }}>🎉 ¡Pago aprobado!</h2>
            <p>Gracias por tu compra. Pronto recibirás confirmación.</p>
          </div>
        );
      case "DECLINED":
        return (
          <div>
            <h2 style={{ color: "red" }}>❌ Transacción rechazada</h2>
            <p>Por favor intenta con otro banco o método.</p>
          </div>
        );
      case "PENDING":
        return <h2 style={{ color: "orange" }}>⏳ Esperando confirmación...</h2>;
      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>⚠️ Referencia no encontrada</h2>;
      default:
        return estado === null
          ? <h2>🔍 Consultando estado del pago…</h2>
          : <h2 style={{ color: "gray" }}>📌 Estado desconocido: {estado}</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>🧾 Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>

      {/* ♿ Accesibilidad: permite que lectores de pantalla detecten cambios */}
      <div aria-live="polite" role="status">
        {renderEstado()}
      </div>

      {/* ⏱️ Info útil para debug o mostrar tiempo al usuario */}
      {ultimaConsulta && (
        <p style={{ fontSize: "0.85rem", color: "#666", marginTop: "0.5rem" }}>
          Última verificación: {ultimaConsulta}
        </p>
      )}

      <button
        onClick={() => window.location.href = "/"}
        style={{ marginTop: "1.5rem", padding: "10px 20px", cursor: "pointer" }}
      >
        🔙 Volver a la tienda
      </button>
    </div>
  );
};

export default EstadoPago;
