import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { CarritoContext } from "../context/CarritoContext"; // üìå Accedemos al estado global del carrito

const Pago = () => {
  const { carrito } = useContext(CarritoContext); // üìå Obtenemos el carrito del contexto
  const [email, setEmail] = useState("");
  const [total, setTotal] = useState(0); // üî• NUEVO: Estado para almacenar el total del carrito

  // üî• NUEVO: Funci√≥n para calcular el total del carrito üî•
  const calcularTotal = () => {
    console.log("Contenido del carrito:", carrito); // üî• Depuraci√≥n: Verifica si el carrito tiene productos
    const totalCalculado = carrito.reduce((total, producto) => total + producto.precio * producto.cantidad, 0);
    console.log("Total calculado:", totalCalculado); // üî• Depuraci√≥n: Muestra el total antes de enviarlo al backend
    return totalCalculado;
  };

  // üî• NUEVO: Actualizar el estado `total` cuando cambie el carrito üî•
  useEffect(() => {
    setTotal(calcularTotal());
  }, [carrito]);

  // üî• NUEVO: Funci√≥n para procesar el pago con PSE üî•
  const pagarConPSE = () => {
    console.log("Enviando pago con datos:", { usuario: email, valor: total }); // üî• Depuraci√≥n: Verifica los datos antes de enviarlos

    axios.post("http://localhost:3000/pago/pse", { usuario: email, valor: total })
      .then(res => alert("‚úÖ Pago iniciado, sigue las instrucciones en PSE."))
      .catch(err => console.error("‚ùå Error al procesar el pago:", err));
  };

  return (
    <div>
      <h2>üí≥ Pagar con PSE</h2>

      {/* üìå Campo para ingresar correo */}
      <input type="email" placeholder="Correo electr√≥nico" onChange={(e) => setEmail(e.target.value)} />

      {/* üî• NUEVO: Mostramos el total calculado antes de pagar üî• */}
      <h3>Total a pagar: ${total.toFixed(2)} COP</h3>

      {/* üî• NUEVO: Bot√≥n para pagar con PSE üî• */}
      <button onClick={pagarConPSE} style={{ backgroundColor: "#4CAF50", color: "white", padding: "10px", border: "none", cursor: "pointer" }}>
        üí∞ Pagar ahora
      </button>
    </div>
  );
};

export default Pago;
