import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const EstadoPago = () => {
  const { reference } = useParams(); // üì¶ Referencia capturada de la URL
  const [estado, setEstado] = useState(null); // üîÅ Estado actual del pago
  const [ultimaConsulta, setUltimaConsulta] = useState(null); // üïì Timestamp de √∫ltima verificaci√≥n

  useEffect(() => {
    let intervalo;
    let delayInicial;

    const consultarEstado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);

        if (res.ok) {
          const data = await res.json();
          setEstado(data.status); // ‚úÖ Almacenamos el estado
          setUltimaConsulta(new Date().toLocaleTimeString()); // üïì Marcamos hora de consulta

          if (data.status !== "PENDING") {
            clearInterval(intervalo); // üõë Detenemos polling si ya tenemos resultado final
          }
        } else if (res.status === 404) {
          setEstado("NO_ENCONTRADO"); // ‚ö†Ô∏è No se encontr√≥ la referencia a√∫n
          clearInterval(intervalo); // Opcional: detener tambi√©n si referencia no existe
        }
      } catch (error) {
        console.error("‚ùå Error consultando estado:", error);
      }
    };

    // ‚è±Ô∏è Esperamos 1.5 segundos antes de hacer la primera consulta (para dar tiempo a Mongo)
    delayInicial = setTimeout(() => {
      consultarEstado();
      intervalo = setInterval(consultarEstado, 3000); // üîÅ Polling cada 3s
    }, 1500);

    // üßº Limpieza de intervalos y timeouts al desmontar
    return () => {
      if (intervalo) clearInterval(intervalo);
      if (delayInicial) clearTimeout(delayInicial);
    };
  }, [reference]);

  // üé® Renderizamos seg√∫n el estado actual
  const renderEstado = () => {
    switch (estado) {
      case "APPROVED":
        return (
          <div>
            <h2 style={{ color: "green" }}>üéâ ¬°Gracias por tu compra!</h2>
            <p>Tu pago fue aprobado exitosamente.</p>
            <p>Te redirigiremos pronto o puedes volver a la tienda ahora mismo.</p>
          </div>
        );
      case "DECLINED":
        return <h2 style={{ color: "red" }}>‚ùå Pago rechazado</h2>;
      case "PENDING":
        return <h2 style={{ color: "orange" }}>‚è≥ Esperando confirmaci√≥n...</h2>;
      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>‚ö†Ô∏è Referencia no encontrada</h2>;
      default:
        return estado === null
          ? <h2>üîç Consultando estado...</h2>
          : <h2 style={{ color: "gray" }}>‚ö†Ô∏è Estado desconocido: {estado}</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>üßæ Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>

      {/* ‚ôø Accesibilidad para lectores de pantalla */}
      <div aria-live="polite">
        {renderEstado()}
      </div>

      {/* üïì Muestra la √∫ltima hora de verificaci√≥n */}
      {ultimaConsulta && (
        <p style={{ fontSize: "0.85rem", color: "#666", marginTop: "0.5rem" }}>
          √öltima consulta: {ultimaConsulta}
        </p>
      )}

      {/* üîÅ Bot√≥n para volver a la tienda */}
      <button
        onClick={() => window.location.href = "/"}
        style={{ marginTop: "1.5rem", padding: "10px 20px", cursor: "pointer" }}
      >
        üîô Volver a la tienda
      </button>
    </div>
  );
};

export default EstadoPago;
