import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import Productos from "./components/Productos";
import Carrito from "./components/Carrito";
import Pago from "./components/Pago";
import EstadoPago from "./components/EstadoPago";
import Categoria from "./components/Categoria";

import { CarritoProvider } from "./context/CarritoContext";
import "./App.css";

const API_URL = process.env.REACT_APP_API_URL;

const App = () => {
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState(null);

  return (
    <CarritoProvider>
      <Router>
        <Routes>
          <Route
            path="/"
            element={
              <div>
                {/* üõçÔ∏è T√≠tulo principal del sitio */}
                <h1
                  style={{
                    textAlign: "center",
                    fontSize: "2.5rem",
                    margin: "2rem 0",
                    color: "#222"
                  }}
                >
                  üõçÔ∏è Wish Marketplace ‚Äì Mercado de deseos
                </h1>

                {/* üóÇÔ∏è Selector de categor√≠a */}
                <Categoria
                  setCategoria={setCategoriaSeleccionada}
                  categoriaSeleccionada={categoriaSeleccionada}
                />

                {/* üõí Listado de productos */}
                <Productos
                  apiUrl={API_URL}
                  categoria={categoriaSeleccionada}
                />

                {/* üß∫ Carrito de compras */}
                <Carrito apiUrl={API_URL} usuarioId={"123456"} />

                {/* üí≥ Formulario de pago */}
                <Pago apiUrl={API_URL} />

                {/* üîî Toasts de feedback visual */}
                <ToastContainer position="top-right" autoClose={3000} />
              </div>
            }
          />

          {/* ‚úÖ Ruta para estado de pago */}
          <Route
            path="/estado/:reference"
            element={<EstadoPago apiUrl={API_URL} />}
          />
        </Routes>
      </Router>
    </CarritoProvider>
  );
};

export default App;
