import React, { useEffect, useState } from "react";

const ResumenPago = ({ reference }) => {
  const [detalle, setDetalle] = useState(null);

  useEffect(() => {
    const obtenerDetalle = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);
        if (res.ok) {
          const data = await res.json();
          setDetalle(data);
        }
      } catch (error) {
        console.error("‚ùå Error obteniendo resumen:", error);
      }
    };

    obtenerDetalle();
  }, [reference]);

  if (!detalle) return null;

  return (
    <div style={{ marginTop: "2rem", fontSize: "0.95rem" }}>
      <h3>üìã Resumen de Transacci√≥n</h3>
      <p><strong>Monto:</strong> ${(detalle.amount_in_cents / 100).toFixed(2)} COP</p>
      <p><strong>Estado:</strong> {detalle.status}</p>
      <p><strong>Referencia:</strong> {detalle.reference}</p>
      <p><strong>Fecha:</strong> {new Date(detalle.createdAt).toLocaleString()}</p>
      {/* Puedes agregar m√°s datos: m√©todo, banco, user_email si lo registras */}
    </div>
  );
};

export default ResumenPago;
