import React, { useEffect, useState } from "react"; // Importamos React, useEffect y useState
import axios from "axios"; // Importamos Axios para hacer solicitudes HTTP

const Productos = () => {
  // Estado para almacenar los productos obtenidos de la API
  const [productos, setProductos] = useState([]);

  // useEffect se ejecuta cuando el componente se monta
  useEffect(() => {
    axios.get("http://localhost:3000/productos") // Hacemos una solicitud GET a nuestra API
      .then(response => {
        setProductos(response.data); // Guardamos los datos de la API en el estado
      })
      .catch(error => {
        console.error("Error al obtener productos:", error); // Capturamos y mostramos errores en la consola
      });
  }, []);

  return (
    <div>
      <h2>Lista de Productos</h2>
      <ul>
        {/* Recorremos el array de productos y los renderizamos dinÃ¡micamente */}
        {productos.map((producto) => (
          <li key={producto.id}>{producto.nombre} - {producto.precio} USD</li>
        ))}
      </ul>
    </div>
  );
};

export default Productos; // Exportamos el componente para usarlo en otros archivos
