import React, { useEffect, useState } from "react";

const ResumenPago = ({ reference }) => {
  const [detalle, setDetalle] = useState(null);
  const [error, setError] = useState(false);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    const obtenerDetalle = async () => {
      try {
        const res = await fetch(`http://localhost:3000/pago/${reference}`);
        if (!res.ok) throw new Error("No se pudo obtener el detalle");
        const data = await res.json();
        setDetalle(data);
      } catch (err) {
        console.error("‚ùå Error obteniendo el resumen:", err);
        setError(true);
      } finally {
        setCargando(false);
      }
    };

    obtenerDetalle();
  }, [reference]);

  if (cargando) return <p>‚è≥ Cargando resumen‚Ä¶</p>;
  if (error || !detalle) return <p style={{ color: "crimson" }}>üö® No se pudo cargar el resumen.</p>;

  return (
    <div style={{
      marginTop: "2rem",
      border: "1px solid #ccc",
      padding: "1rem",
      borderRadius: "8px",
      backgroundColor: "#f9f9f9",
      maxWidth: "450px",
      marginLeft: "auto",
      marginRight: "auto"
    }}>
      <h3 style={{ marginBottom: "1rem" }}>üìã Detalles del Pago</h3>

      <p><strong>Referencia:</strong> {detalle.reference}</p>
      <p><strong>Estado:</strong> {detalle.status}</p>
      <p>
        <strong>Monto:</strong>{" "}
        {detalle.amount_in_cents
          ? `$ ${(detalle.amount_in_cents / 100).toLocaleString("es-CO", { minimumFractionDigits: 2 })} COP`
          : "‚Äî"}
      </p>
      <p>
        <strong>Fecha:</strong>{" "}
        {detalle.createdAt
          ? new Date(detalle.createdAt).toLocaleString("es-CO")
          : "No registrada"}
      </p>

      {/* Campos adicionales opcionales si los tienes en Mongo */}
      {detalle.bank_name && <p><strong>Banco:</strong> {detalle.bank_name}</p>}
      {detalle.user_email && <p><strong>Correo:</strong> {detalle.user_email}</p>}
      {detalle.payment_method_type && <p><strong>M√©todo:</strong> {detalle.payment_method_type.toUpperCase()}</p>}
      {detalle.attempts && <p><strong>Intentos:</strong> {detalle.attempts}</p>}
      {detalle.reject_reason && <p><strong>Motivo del rechazo:</strong> {detalle.reject_reason}</p>}
    </div>
  );
};

export default ResumenPago;
