import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ResumenPago from "./ResumenPago";

const EstadoPago = ({ apiUrl }) => {
  const { reference } = useParams();
  const navigate = useNavigate();
  const [estado, setEstado] = useState(null);
  const [ultimaConsulta, setUltimaConsulta] = useState(null);
  const [errorConsulta, setErrorConsulta] = useState(false);
  const [pago, setPago] = useState(null);

  useEffect(() => {
    if (!apiUrl || !reference) {
      console.warn("‚ö†Ô∏è apiUrl o reference no est√°n definidos en EstadoPago");
      return;
    }

    let intervalo;
    let delayInicial;

    const consultarEstado = async () => {
      try {
        const res = await fetch(`${apiUrl}/pago/${reference}`);
        if (!res.ok) {
          if (res.status === 404) {
            setEstado("NO_ENCONTRADO");
          } else {
            setErrorConsulta(true);
          }
          clearInterval(intervalo);
          return;
        }

        const data = await res.json();
        console.log("üì• Estado del pago:", data.status);
        console.log("üßæ Detalles del pago:", data);

        setEstado(data.status);
        setPago(data);
        setUltimaConsulta(new Date().toLocaleTimeString());

        if (data.status !== "PENDING") {
          clearInterval(intervalo);
        }
      } catch (error) {
        console.error("‚ùå Error en la consulta:", error);
        setErrorConsulta(true);
        clearInterval(intervalo);
      }
    };

    delayInicial = setTimeout(() => {
      consultarEstado();
      intervalo = setInterval(consultarEstado, 3000);
    }, 1500);

    return () => {
      clearInterval(intervalo);
      clearTimeout(delayInicial);
    };
  }, [reference, apiUrl]);

  const renderEstado = () => {
    if (errorConsulta) {
      return <h2 style={{ color: "crimson" }}>üö® Error al consultar el estado del pago</h2>;
    }

    switch (estado) {
      case "APPROVED":
        return (
          <div>
            <h2 style={{ color: "green" }}>üéâ ¬°Pago aprobado!</h2>
            <p>Gracias por tu compra.</p>
            <ResumenPago pago={pago} />
          </div>
        );

      case "DECLINED":
        return (
          <div>
            <h2 style={{ color: "red" }}>‚ùå Transacci√≥n rechazada</h2>
            <p>Por favor intenta nuevamente con otro banco.</p>
          </div>
        );

      case "PENDING":
        return (
          <div>
            <h2 style={{ color: "orange" }}>‚è≥ Esperando confirmaci√≥n del banco‚Ä¶</h2>
            <div className="spinner" style={{
              margin: "1rem auto",
              width: "40px",
              height: "40px",
              border: "4px solid #ccc",
              borderTop: "4px solid orange",
              borderRadius: "50%",
              animation: "spin 1s linear infinite"
            }} />
          </div>
        );

      case "NO_ENCONTRADO":
        return <h2 style={{ color: "gray" }}>‚ö†Ô∏è Referencia no encontrada</h2>;

      case null:
        return (
          <div>
            <h2>üîç Consultando estado del pago‚Ä¶</h2>
            <div className="spinner" />
          </div>
        );

      default:
        return <h2 style={{ color: "gray" }}>üìå Estado desconocido: {estado}</h2>;
    }
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>üßæ Estado del Pago</h1>
      <p>Referencia: <strong>{reference}</strong></p>

      <div aria-live="polite" role="status">
        {renderEstado()}
      </div>

      {ultimaConsulta && (
        <p style={{ fontSize: "0.85rem", color: "#666", marginTop: "0.5rem" }}>
          √öltima verificaci√≥n: {ultimaConsulta}
        </p>
      )}

      <button
        onClick={() => navigate("/")}
        style={{
          marginTop: "1.5rem",
          padding: "10px 20px",
          cursor: "pointer"
        }}
      >
        üîô Volver a la tienda
      </button>

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default EstadoPago;
